function createTournament(playerFighter, config, data) {
const top100 = data.gameState.top100Fighters[playerFighter.id] || {};
const allFighters = Object.values(top100);
const participants = [{...playerFighter, isPlayer: true}];
const usedNames = new Set([playerFighter.firstName + playerFighter.nickname]);
while (participants.length < config.participants && allFighters.length > 0) {
const randomIndex = Math.floor(Math.random() * allFighters.length);
const opponent = allFighters[randomIndex];
const name = opponent.name || (opponent.firstName + opponent.nickname);
if (!usedNames.has(name)) {
participants.push({...opponent, isPlayer: false});
usedNames.add(name);
}
}
for (let i = participants.length - 1; i > 0; i--) {
const j = Math.floor(Math.random() * (i + 1));
[participants[i], participants[j]] = [participants[j], participants[i]];
}
return {
name: config.name,
participants: config.participants,
prize: config.prize,
fighters: participants,
currentRound: 1,
bracket: createBracket(participants),
playerAdvanced: true,
roundNames: ['', '', '']
};
}
function createBracket(fighters) {
const rounds = Math.log2(fighters.length);
const bracket = [];
const round1 = [];
for (let i = 0; i < fighters.length; i += 2) {
round1.push({ fighter1: fighters[i], fighter2: fighters[i + 1], winner: null, simulated: false });
}
bracket.push(round1);
for (let r = 1; r < rounds; r++) {
const round = [];
const matchesCount = Math.pow(2, rounds - r - 1);
for (let i = 0; i < matchesCount; i++) {
round.push({ fighter1: null, fighter2: null, winner: null, simulated: false });
}
bracket.push(round);
}
return bracket;
}
