function advanceToNextRound(tournament) {
const currentRound = tournament.bracket[tournament.currentRound - 1];
const winners = currentRound.map(m => m.winner);
const playerWon = winners.some(w => w.isPlayer);
if (!playerWon) {
endTournament(tournament, false);
return;
}
if (currentRound.length === 1) {
endTournament(tournament, winners[0].isPlayer);
return;
}
const nextRound = tournament.bracket[tournament.currentRound];
for (let i = 0; i < nextRound.length; i++) {
nextRound[i].fighter1 = winners[i * 2];
nextRound[i].fighter2 = winners[i * 2 + 1];
}
tournament.currentRound++;
const data = loadGameSync();
if (tournament.currentRound === tournament.bracket.length - 1) {
data.gameState.tournaments.stats.semifinals++;
}
if (tournament.currentRound === tournament.bracket.length) {
data.gameState.tournaments.stats.finals++;
}
saveGameSync(data);
showTournamentBracket(tournament);
}
