function generateFighters() {
const firstNames = ['', '', '', '', '', '', '', ''];
const lastNames = ['', '', '', '', '', '', '', ''];
const nicknames = ['', '', '', '', '', '', '', ''];
const avatars = BOXER_AVATARS;
const fighters = [];
const numFighters = 3;
for (let i = 0; i < numFighters; i++) {
let talent;
const rand = Math.random();
if (rand < 0.01) {
talent = 10;
} else if (rand < 0.10) {
talent = Math.random() < 0.5 ? 8 : 9;
} else if (rand < 0.40) {
talent = Math.random() < 0.5 ? 6 : 7;
} else {
talent = Math.floor(Math.random() * 3) + 3;
}
const age = Math.floor(Math.random() * 8) + 18;
const weight = Math.floor(Math.random() * 20) + 65;
const baseStats = 30 - (talent * 2);
const variance = 5;
const avatarIndex = Math.floor(Math.random() * avatars.length);
fighters.push({
id: i,
firstName: firstNames[Math.floor(Math.random() * firstNames.length)],
lastName: lastNames[Math.floor(Math.random() * lastNames.length)],
nickname: nicknames[Math.floor(Math.random() * nicknames.length)],
avatarIndex: avatarIndex,
age: age,
weight: weight,
talent: talent,
stats: {
power: Math.max(5, Math.min(30, baseStats + Math.floor(Math.random() * variance))),
speed: Math.max(5, Math.min(30, baseStats + Math.floor(Math.random() * variance))),
stamina: Math.max(5, Math.min(30, baseStats + Math.floor(Math.random() * variance))),
defense: Math.max(5, Math.min(30, baseStats + Math.floor(Math.random() * variance)))
}
});
}
renderFighters(fighters);
}
